
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int age = 5;
		
		if (age < 18) {
			System.out.println("You are under 18");
		} else if (age == 18) {
			System.out.println("You are exactly 18");
		} else if (age > 18) {
			System.out.println("You are over 18");
		}
	}

}
