
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String example = "SomethingForExample";
		
		System.out.println("The example is: " + example);
		System.out.println("The length is: " + example.length());
		System.out.println("Upper case: " + example.toUpperCase());
		System.out.println("Lower case: " + example.toLowerCase());
		System.out.println("First character: " + example.charAt(0));
		System.out.println("Index of S: " + example.indexOf('S'));
		System.out.println("Replace S with character ~: " + example.replace('S', '~'));
		
	}

}
